import { Component, OnInit, Inject } from '@angular/core';
import { TransactionsService, ITransactionService } from './transactions.service';
import { MatDialog } from '@angular/material/dialog';
import { TransactionDetailComponent } from './transaction-detail/transaction-detail.component';
import { map, Observable } from 'rxjs';

@Component({
  selector: 'app-transaction-list',
  templateUrl: './transaction-list.component.html',
  styleUrls: ['./transaction-list.component.scss']
})
export class TransactionListComponent implements OnInit {
  constructor(
    private transactionData: TransactionsService,
    public dialog: MatDialog
  ) { }

  //transactionList: any = [];
  transactionList$?: Observable<ITransactionService[]>;
  startDate: string = "1970-01-01";
  endDate: any = this.convertDate2(new Date());

  ngOnInit(): void {
    this.getTransactionList();
  }

  getTransactionList() {
    this.transactionList$ = this.transactionData.getTransactions(this.startDate, this.endDate).pipe(
      map((response) => {
        if(Array.isArray(response)) {
          return response.filter(response => response.status !== 'PENDING');
        }
        return [];
      })
    );
  }

  convertDate(milliseconds: any) {
    let date = new Date(milliseconds)
    let dd = String(date.getDate()).padStart(2, '0');
    let mm = String(date.getMonth() + 1).padStart(2, '0');
    let yyyy = date.getFullYear();

    let dateFormated = dd + '/' + mm + '/' + yyyy;
    return dateFormated;
  }

  convertDate2(date: any) {
    return date.toISOString().split('T')[0];
  }

  dateChanged($event: any, isStartDate: Boolean) {
    if (isStartDate) {
      this.startDate = this.convertDate2($event.target.value);
    } else {
      this.endDate = this.convertDate2($event.target.value);
    }
  }

  openDialog(transaction: any): void {
    const dialogRef = this.dialog.open(TransactionDetailComponent, {
      width: '600px',
      data: transaction
    });

    dialogRef.afterClosed().subscribe(result => {
      console.log('closed', result)
    });
  }

}
<div>
    <mat-form-field appearance="fill">
        <mat-label>Enter a date range</mat-label>
        <mat-date-range-input [rangePicker]="picker">
            <input matStartDate placeholder="Start date" (dateChange)="dateChanged($event, true)">
            <input matEndDate   placeholder="End date"   (dateChange)="dateChanged($event, false)">
        </mat-date-range-input>
        <mat-hint>MM/DD/YYYY – MM/DD/YYYY</mat-hint>
        <mat-datepicker-toggle matSuffix [for]="picker"></mat-datepicker-toggle>
        <mat-date-range-picker #picker></mat-date-range-picker>
    </mat-form-field>
    <button mat-raised-button color="primary" (click)="getTransactionList()">GO!</button>

    <ng-template let-transactionList [ngIf]="(transactionList$ | async)">
        <table *ngIf="transactionList && transactionList.length > 0; else elseBlock">
            <thead>
                <th>ID</th>
                <th>Date</th>
                <th>Comments</th>
                <th>Action</th>
            </thead>
            <tbody>
                <tr *ngFor="let transaction of transactionList">
                    <td>{{transaction.id}}</td>
                    <td>{{convertDate(transaction.date)}}</td>
                    <td>{{transaction.Comments}}</td>
                    <td><button (click)="openDialog(transaction)">View</button></td>
                </tr>
            </tbody>
        </table>
        <ng-template #elseBlock>
            <div>
                No Transactions Found!
            </div>
        </ng-template>
    </ng-template>


</div>
